'use strict';

var util = require('util');

var Network = require('../index').Network;

function Visudream(options) {
  Network.call(this, 'visudream', options);
}

util.inherits(Visudream, Network);

Visudream.prototype.getDevice = function (addr, options, cb) {
  if (typeof options === 'function') {
    cb = options;
  }
  return cb && cb();
};

Visudream.prototype.discover = function (driverOrModel, cb) {
  return cb && cb();
};

module.exports = new Visudream();
